  .news .watch_started .Box,
  .news .watch_started .text-gray,
  .news .fork .border,
  .news .fork .Box,
  .news .tag .Box,
  .news .git-branch .Box,
  .news .repo .Box .f6,
  .news .public .border .f6,
  .news .public .Box .f6,
  .news .issues_comment .message,
  .news .commit_comment .message,
  .news .issues_comment .Details .mt-2,
  .news .commit_comment .Details .mt-2,
  .news .issues_opened .Box div.lh-condensed,
  .news .issues_closed .Box div.lh-condensed,
  .news .issues_closed .Box .f6,
  .news .issues_closed .border .f6,
  .news .issues_reopened .Box div.lh-condensed,
  .news .issues_reopened .Box .f6,
  .news .border .diffstat,
  .news .follow .Box {
    display: none !important;
  }
  .news > div .Details:not(.Details--on) div.Details-content--hidden,
  .news .fork .Details .Box,
  .news .watch_started .Details .Box,
  .news .create .Details .d-flex .Box,
  .news .create .starring-container button[data-hydro-click] {
    display: block !important;
  }
  .news .watch_started .Details .d-flex .Box,
  .news .issues_opened .Box,
  .news .issues_closed .Box,
  .news .issues_reopened .Box {
    border: 0 !important;
    background: transparent !important;
    padding: 0 !important;
    margin-bottom: 0 !important;
  }
  .news .p-3,
  .news .border.p-3,
  .news .repo .Box.p-3,
  .news .public .border.p-3,
  .news .public .Box.p-3 {
    padding-bottom: 5px !important;
    padding-top: 5px !important;
  }
  .news p {
    margin-bottom: 0 !important;
  }
  .news .issues_opened .Box a,
  .news .issues_closed .Box a {
    display: inline-block !important;
    white-space: nowrap !important;
    max-width: 500px !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
  }
  .news .gollum .text-gray .mt-2 {
    display: inline-block !important;
  }
  .news .push .link-gray {
    float: right !important;
    top: -2em !important;
    position: relative !important;
  }

