  }
  pre.highlight .pi {
    color: var(--red) !important;
  }
  pre.highlight .no {
    color: var(--violet) !important;
  }
  pre.highlight .na {
    color: var(--lighter) !important;
  }
  pre.highlight .s1,
  pre.highlight .s2 {
    color: var(--green) !important;
  }
  pre.highlight .kp,
  pre.highlight .k {
